import React, { useState, useEffect } from 'react';
import { Github, Mail, MapPin, Star, GitFork, ExternalLink, Users, Code, Loader } from 'lucide-react';

export default function Portfolio() {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPortfolio();
  }, []);

  const fetchPortfolio = async () => {
    try {
      const response = await fetch('/api/portfolio');
      if (!response.ok) throw new Error(`API Error: ${response.status}`);

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('API returned HTML instead of JSON - check if Flask is running on port 5000');
      }

      const data = await response.json();
      setPortfolio(data);
    } catch (err) {
      setError(err.message);
      console.error('Portfolio fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <Loader className="w-8 h-8 animate-spin text-blue-400 mx-auto mb-4" />
          <p className="text-gray-400">Loading portfolio...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-400 mb-4">Error: {error}</p>
          <button
            onClick={fetchPortfolio}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Hero Section */}
      <section className="relative min-h-screen flex items-center justify-center px-4">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 to-purple-900/20"></div>
        <div className="relative z-10 text-center max-w-4xl mx-auto">
          {portfolio?.avatar_url && (
            <img
              src={portfolio.avatar_url}
              alt={portfolio.name}
              className="w-32 h-32 rounded-full mx-auto mb-8 border-4 border-blue-400/30 shadow-2xl"
            />
          )}
          <h1 className="text-5xl md:text-7xl font-bold mb-6 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            {portfolio?.name || 'Developer'}
          </h1>
          <p className="text-2xl md:text-3xl text-gray-300 mb-6">
            {portfolio?.title}
          </p>
          <p className="text-lg text-gray-400 mb-8 max-w-2xl mx-auto leading-relaxed">
            {portfolio?.bio}
          </p>

          {/* Stats */}
          <div className="flex justify-center space-x-8 mb-8">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-400">{portfolio?.public_repos || 0}</div>
              <div className="text-sm text-gray-400">Repositories</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-yellow-400">{portfolio?.total_stars || 0}</div>
              <div className="text-sm text-gray-400">Stars</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-400">{portfolio?.followers || 0}</div>
              <div className="text-sm text-gray-400">Followers</div>
            </div>
          </div>

          {/* Contact Links */}
          <div className="flex justify-center space-x-6">
            {portfolio?.github_url && (
              <a
                href={portfolio.github_url}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center space-x-2 px-6 py-3 bg-gray-800 hover:bg-gray-700 rounded-lg transition-colors"
              >
                <Github className="w-5 h-5" />
                <span>GitHub</span>
              </a>
            )}
            {portfolio?.email && (
              <a
                href={`mailto:${portfolio.email}`}
                className="flex items-center space-x-2 px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
              >
                <Mail className="w-5 h-5" />
                <span>Contact</span>
              </a>
            )}
          </div>
        </div>
      </section>

      {/* Skills Section */}
      {portfolio?.skills && (
        <section className="py-20 px-4">
          <div className="max-w-6xl mx-auto">
            <h2 className="text-4xl font-bold text-center mb-12 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              Skills & Technologies
            </h2>
            <div className="flex flex-wrap justify-center gap-4">
              {portfolio.skills.map((skill, index) => (
                <span
                  key={index}
                  className="px-4 py-2 bg-gray-800 hover:bg-gray-700 rounded-full text-gray-300 transition-colors cursor-default"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>
        </section>
      )}

      {/* Projects Section */}
      {portfolio?.projects && portfolio.projects.length > 0 && (
        <section className="py-20 px-4 bg-gray-800/50">
          <div className="max-w-6xl mx-auto">
            <h2 className="text-4xl font-bold text-center mb-12 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              Featured Projects
            </h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {portfolio.projects.map((project, index) => (
                <div
                  key={index}
                  className="bg-gray-800 rounded-lg p-6 hover:bg-gray-750 transition-colors border border-gray-700 hover:border-gray-600"
                >
                  <div className="flex justify-between items-start mb-4">
                    <h3 className="text-xl font-semibold text-white">{project.name}</h3>
                    <div className="flex space-x-2">
                      {project.html_url && (
                        <a
                          href={project.html_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-gray-400 hover:text-white transition-colors"
                        >
                          <Github className="w-5 h-5" />
                        </a>
                      )}
                      {project.homepage && (
                        <a
                          href={project.homepage}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-gray-400 hover:text-white transition-colors"
                        >
                          <ExternalLink className="w-5 h-5" />
                        </a>
                      )}
                    </div>
                  </div>

                  <p className="text-gray-400 mb-4 text-sm leading-relaxed">
                    {project.description}
                  </p>

                  <div className="flex justify-between items-center">
                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      <span className="flex items-center space-x-1">
                        <Code className="w-4 h-4" />
                        <span>{project.language}</span>
                      </span>
                      <span className="flex items-center space-x-1">
                        <Star className="w-4 h-4" />
                        <span>{project.stars}</span>
                      </span>
                      <span className="flex items-center space-x-1">
                        <GitFork className="w-4 h-4" />
                        <span>{project.forks}</span>
                      </span>
                    </div>
                  </div>

                  {project.topics && project.topics.length > 0 && (
                    <div className="mt-4 flex flex-wrap gap-2">
                      {project.topics.slice(0, 3).map((topic, i) => (
                        <span
                          key={i}
                          className="px-2 py-1 bg-blue-600/20 text-blue-300 text-xs rounded"
                        >
                          {topic}
                        </span>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </section>
      )}

      {/* Footer */}
      <footer className="py-12 px-4 border-t border-gray-800">
        <div className="max-w-6xl mx-auto text-center">
          <div className="flex justify-center items-center space-x-4 mb-4">
            {portfolio?.location && (
              <span className="flex items-center space-x-1 text-gray-400">
                <MapPin className="w-4 h-4" />
                <span>{portfolio.location}</span>
              </span>
            )}
          </div>
          <p className="text-gray-500 text-sm">
            Built with React & Flask â€¢ Data fetched from GitHub API
          </p>
        </div>
      </footer>
    </div>
  );
}
