import React, { useState, useEffect } from 'react';
import { Github, Mail, MapPin, Star, GitFork, ExternalLink, Users, Code, Loader, Folder } from 'lucide-react';

export default function Portfolio() {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPortfolio();
  }, []);

  const fetchPortfolio = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/portfolio');
      if (!response.ok) throw new Error(`API Error: ${response.status}`);

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('API returned HTML instead of JSON - check if Flask is running on port 5000');
      }

      const data = await response.json();
      setPortfolio(data);
    } catch (err) {
      setError(err.message);
      console.error('Portfolio fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <Loader className="w-8 h-8 text-gray-400 animate-spin mx-auto mb-4" />
          <p className="text-gray-300">Loading portfolio...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-400 mb-4">Error: {error}</p>
          <button
            onClick={fetchPortfolio}
            className="px-4 py-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 transition-colors"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white flex">
      {/* Left Sidebar */}
      <div className="w-80 bg-gray-800 border-r border-gray-700 flex flex-col">
        <div className="p-8 flex-1">
          {/* Profile Section */}
          <div className="text-center mb-8">
            {portfolio?.avatar_url && (
              <img
                src={portfolio.avatar_url}
                alt={portfolio.name}
                className="w-32 h-32 rounded-full mx-auto mb-6 border-4 border-gray-600 shadow-xl"
              />
            )}
            <h1 className="text-2xl font-bold text-white mb-2">
              {portfolio?.name || 'Adi Pardeshi'}
            </h1>
            {portfolio?.title && (
              <p className="text-gray-300 text-lg mb-3">{portfolio.title}</p>
            )}
            {portfolio?.bio && (
              <p className="text-gray-400 text-sm leading-relaxed mb-6">{portfolio.bio}</p>
            )}
          </div>

          {/* Stats */}
          <div className="space-y-4 mb-8">
            <div className="flex items-center justify-between p-4 bg-gray-700 rounded-lg">
              <div className="flex items-center space-x-3">
                <Code className="w-5 h-5 text-gray-400" />
                <span className="text-gray-300">Repositories</span>
              </div>
              <span className="text-xl font-semibold text-white">
                {portfolio?.public_repos || 0}
              </span>
            </div>

            <div className="flex items-center justify-between p-4 bg-gray-700 rounded-lg">
              <div className="flex items-center space-x-3">
                <Star className="w-5 h-5 text-gray-400" />
                <span className="text-gray-300">Stars</span>
              </div>
              <span className="text-xl font-semibold text-white">
                {portfolio?.total_stars || 0}
              </span>
            </div>

            <div className="flex items-center justify-between p-4 bg-gray-700 rounded-lg">
              <div className="flex items-center space-x-3">
                <Users className="w-5 h-5 text-gray-400" />
                <span className="text-gray-300">Followers</span>
              </div>
              <span className="text-xl font-semibold text-white">
                {portfolio?.followers || 0}
              </span>
            </div>
          </div>

          {/* Contact Links */}
          <div className="space-y-3 mb-8">
            {portfolio?.github_url && (
              <a
                href={portfolio.github_url}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center space-x-3 p-3 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors"
              >
                <Github className="w-5 h-5 text-gray-400" />
                <span className="text-gray-300">GitHub Profile</span>
              </a>
            )}
            {portfolio?.email && (
              <a
                href={`mailto:${portfolio.email}`}
                className="flex items-center space-x-3 p-3 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors"
              >
                <Mail className="w-5 h-5 text-gray-400" />
                <span className="text-gray-300">Send Email</span>
              </a>
            )}
          </div>

          {/* Skills */}
          {portfolio?.skills && (
            <div>
              <h3 className="text-lg font-semibold text-white mb-4">Skills</h3>
              <div className="flex flex-wrap gap-2">
                {portfolio.skills.map((skill, index) => (
                  <span
                    key={index}
                    className="px-3 py-1 bg-gray-700 text-gray-300 text-sm rounded-full hover:bg-gray-600 transition-colors"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="p-6 border-t border-gray-700">
          {portfolio?.location && (
            <div className="flex items-center space-x-2 text-gray-400 mb-3">
              <MapPin className="w-4 h-4" />
              <span className="text-sm">{portfolio.location}</span>
            </div>
          )}
          <p className="text-xs text-gray-500">
            Built with React & Flask
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-y-auto">
        <div className="p-8">
          {/* Projects Section */}
          <div className="mb-8">
            <div className="flex items-center space-x-3 mb-8">
              <Folder className="w-8 h-8 text-gray-400" />
              <h2 className="text-3xl font-bold text-white">Projects</h2>
              {portfolio?.projects && (
                <span className="px-3 py-1 bg-gray-700 text-gray-300 text-sm rounded-full">
                  {portfolio.projects.length}
                </span>
              )}
            </div>

            {!portfolio?.projects || portfolio.projects.length === 0 ? (
              <div className="text-center py-16">
                <Folder className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-400 mb-2">No Projects Yet</h3>
                <p className="text-gray-500">
                  Projects will appear here once they're available from the API.
                </p>
              </div>
            ) : (
              <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
                {portfolio.projects.map((project, index) => (
                  <div
                    key={index}
                    className="bg-gray-800 border border-gray-700 rounded-xl p-6 hover:bg-gray-750 hover:border-gray-600 transition-all duration-200"
                  >
                    <div className="flex justify-between items-start mb-4">
                      <h3 className="text-xl font-semibold text-white truncate pr-4">
                        {project.name}
                      </h3>
                      <div className="flex space-x-2 flex-shrink-0">
                        {project.html_url && (
                          <a
                            href={project.html_url}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-gray-400 hover:text-white transition-colors"
                          >
                            <Github className="w-5 h-5" />
                          </a>
                        )}
                        {project.homepage && (
                          <a
                            href={project.homepage}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-gray-400 hover:text-white transition-colors"
                          >
                            <ExternalLink className="w-5 h-5" />
                          </a>
                        )}
                      </div>
                    </div>

                    <p className="text-gray-400 text-sm mb-4 line-clamp-3">
                      {project.description || 'No description available'}
                    </p>

                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4 text-sm text-gray-500">
                        {project.language && (
                          <span className="flex items-center space-x-1">
                            <Code className="w-4 h-4" />
                            <span>{project.language}</span>
                          </span>
                        )}
                        <span className="flex items-center space-x-1">
                          <Star className="w-4 h-4" />
                          <span>{project.stars || 0}</span>
                        </span>
                        <span className="flex items-center space-x-1">
                          <GitFork className="w-4 h-4" />
                          <span>{project.forks || 0}</span>
                        </span>
                      </div>
                    </div>

                    {project.topics && project.topics.length > 0 && (
                      <div className="flex flex-wrap gap-2">
                        {project.topics.slice(0, 4).map((topic, i) => (
                          <span
                            key={i}
                            className="px-2 py-1 bg-gray-700 text-gray-300 text-xs rounded-md"
                          >
                            {topic}
                          </span>
                        ))}
                        {project.topics.length > 4 && (
                          <span className="px-2 py-1 bg-gray-700 text-gray-400 text-xs rounded-md">
                            +{project.topics.length - 4} more
                          </span>
                        )}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
